import {Avatar,Grid,Name, memo, useSelector ,Cloth, AvatarClient, Lable, useState} from "../../importAll";
// import { GetGirlImg } from "../../../../Images/Images";

const Inventory = memo((props: IInventoryProps)=>{

    let settings      = useSelector<IStore,ISettings>(store=>store.settings)
    let activeAvatar  = useSelector<IStore,AvatarClient>(store=>store.activeAvatar);
    let [category , setCategory] = useState<"hat"|"shirt"|"shoes"|"pants">("hat")

    const AVATAR_VIEW_GRID      = settings.AVATAR_VIEW_GRID;
    const AVATAR_BODY_POSITION  = activeAvatar.gender=="girl"? settings.GIRL_AVATAR_BODY_POSITION   : settings.BOY_AVATAR_BODY_POSITION ;
    const SHIRT_POSITION        = activeAvatar.gender=="girl"? settings.GIRL_SHIRT_POSITION         : settings.BOY_SHIRT_POSITION       ;
    const SHOES_POSITION        = activeAvatar.gender=="girl"? settings.GIRL_SHOES_POSITION         : settings.BOY_SHOES_POSITION       ;
    const PANTS_POSITION        = activeAvatar.gender=="girl"? settings.GIRL_PANTS_POSITION         : settings.BOY_PANTS_POSITION       ;

    return(
        <Grid rows={4} cols={10}>

            <Lable onClick={()=>{}}>כובעים</Lable>
            <Lable onClick={()=>{}}>חולצות</Lable>
            <Lable onClick={()=>{}}>מכנסיים</Lable>
            <Lable onClick={()=>{}}>נעליים</Lable>

        </Grid>
    )

})

interface IInventoryProps extends IGlobalProps{
    items : typeof ItemClient;
}
export default Inventory;

