import { Avatar_Give_Me_List } from "../../Socket/AvatarSocket";
import {memo, useSelector, GameCreateAvatar ,GameSelectAvatar, useEffect, socket, useStore,AvatarClient, useState, useEffectV2, Container, Settings} from "./../../importAll";
import MusicEffect from "./comps/MusicEffect";


const Game = memo(()=>{
    let settings  = useSelector<IStore,Settings>(store=>store.settings)
    let mainPage                    = useSelector<IStore,any>(store=>store.mainPage)
    let subPage :AllGamePageType    = useSelector<IStore,any>(store=>store.subPage)
    let store                       = useStore();
    let [isloading , setIsLoading]  = useState<boolean>(true);
    

    useEffectV2("Game",()=>Avatar_Give_Me_List(store,setIsLoading)) 
    

    return isloading? 
        <>Wait</>
    : ( 
         <Container Grid={49} width="110vh" height="90vh" margin="10px auto" border> 
            {(mainPage != "Game"              )? <>אין לך גישה </>  :""}
            {(subPage  == "Game-SelectAvatar" )? <GameSelectAvatar/> :""}
            {(subPage  == "Game-CreateAvatar" )? <GameCreateAvatar/> :""}
            <MusicEffect/>
        </Container>
    )
});

export default Game;
