import { GridBtn } from "../../../devComponent";
import {AvatarClient, Button, Img, Flex, Grid, Lable, memo, Icon,Name, AvatarView,GetImage} from "../../../importAll";

const hpIcon             = GetImage("icon","hp");          
const strongIcon         = GetImage("icon","strong");              
const energyIcon         = GetImage("icon","energy");              
const refillEnergyIcon   = GetImage("icon","refillEnergy");                  
const silverIcon         = GetImage("icon","silver");              
const goldIcon           = GetImage("icon","gold");          
const redPowderIcon      = GetImage("icon","redPowder");              
const diamondIcon        = GetImage("icon","diamond");              
const trashIcon          = GetImage("icon","trash");              

const AvatarSelector = memo((props:IAvatarSelectorProps)=>{
    const deleteAvatar = ()=>{}

    return (
        <Grid className="avatarSelector" {...props} rows={50} columns={50} width="95%" height="15%" margin="10px auto" >
            <Name family="cursive" XYcenter start="18,2" end="33,14" rtl > {props.avatar.name}</Name>

            <AvatarView start="39,2" end="50,50" border/>

            <Flex wrap rtl start="12,20" end="37,34" >
                <AvatarSelectorValue icon={hpIcon}           value="3333" width="24%" marginBottom={5}/>
                <AvatarSelectorValue icon={strongIcon}       value="3333" width="24%" marginBottom={5}/>
                <AvatarSelectorValue icon={energyIcon}       value="3333" width="24%" marginBottom={5}/>
                <AvatarSelectorValue icon={refillEnergyIcon} value="3333" width="24%" marginBottom={5}/>
                <AvatarSelectorValue icon={silverIcon}       value="3333" width="24%" />
                <AvatarSelectorValue icon={goldIcon}         value="3333" width="24%" />
                <AvatarSelectorValue icon={redPowderIcon}    value="3333" width="24%" />
                <AvatarSelectorValue icon={diamondIcon}      value="3333" width="24%" />
            </Flex>

            <Lable start="4,6" end="12,15" rtl XYcenter family="cursive">fire</Lable>
            <Img bgImg={GetImage("magic",props.avatar.magicName)} XYcenter start="5,17" end="11,49" />

            <Button onClick={deleteAvatar} start="1,1" end="3,50" bgImg={trashIcon} bgColor="red" />
        </Grid>
    )
})

interface IAvatarSelectorProps extends IGlobalProps{
    avatar:AvatarClient; 
}
export default AvatarSelector;




function AvatarSelectorValue(props:IAvatarSelectorValueProps){
    return(
        <Flex rtl {...props}>
            <Icon bgImg={props.icon} width="30%"/> 
            <Lable family="cursive" XYcenter width="70%" ltr fSize={11}>{props.value}</Lable>
        </Flex>
    )
}

interface IAvatarSelectorValueProps extends IGlobalProps{
    icon:any; 
}
