import { GridBtn } from "../../../devComponent";
import {Dress, Div,Avatar,Grid,Name, memo, useSelector } from "../../../importAll";

const CONTAINER_AVATAR_VIEW_ROW = 30;
const CONTAINER_AVATAR_VIEW_COL = 30;

const AVATAR_VIEW_ROW = 500;
const AVATAR_VIEW_COL = 100;

const START_AVATAR_VIEW = "1,6";
const END_AVATAR_VIEW   = "30,30";

const START_AVATAR  = "20,25";
const END_AVATAR    = "90,500";

const START_DRESS  = "25,100";
const END_DRESS    = "90,200";



const girl  = require("../../../Images/girl/girlAvatar.png")
const dress = require("../../../Images/girl/shirt/shirt6.png")

const AvatarView = memo((props:IAvatarViewProps)=>{
    let avatarName = useSelector<IStore,string>(store=>store.avatarName)
    return(
        <Grid {...props}  rows={30} columns={30} className="avatarView"  >

            <Name XYcenter fSize="150%" start="1,2" end="30,4">{avatarName||"avatar name"}</Name>

            <Div Grid className="avatarViewDev"  start={START_AVATAR_VIEW} end={END_AVATAR_VIEW}  rows={500} columns={100} border>

                    <Avatar src={girl}  start={START_AVATAR} end={END_AVATAR} border />
                    <Dress  src={dress} start={START_DRESS}  end={END_DRESS}  border />

            </Div>

            <GridBtn ForClassName="avatarView" top={4}/>
            <GridBtn ForClassName="avatarViewDev" top={5}/>
        </Grid>
    )
})


interface IAvatarViewProps extends IGlobalProps{}

export default AvatarView
