import React from "react";
import { myStore, usePermission, useSelector } from "./importAll";

export default class Component{
    public Comp :(props:any)=>JSX.Element;
    

    constructor(Comp:(props:any)=>JSX.Element ,permissions?:boolean[]){
        this.Comp = Comp;
    }



    public Get(permissions:IUsePermission={}):(props:any)=>React.MemoExoticComponent<() => JSX.Element>{
        let Com = this.Comp
        
        return (props:any) => {
            let pre = usePermission(permissions)
            if(pre)
                return  React.memo(()=><Com {...props}/>)
            else 
                return React.memo(()=><>X</>)
        }           
    }


}

interface IPermissions{
    Login :boolean,
    Logout :boolean,
    mainPage : AllMainPagesType,
    subPage : AllSubPagesType;
}
