import { useSelector } from "react-redux"

export default function usePermission(obj:IUsePermission):boolean{
    let _mainPage   = useSelector<IStore,AllMainPagesType>(store=>store.mainPage)
    let _subPage    = useSelector<IStore,AllSubPagesType>(store=>store.subPage)
    let _isLogin    = useSelector<IStore,boolean>(store=>store.isLogin)

    if(obj.isLogin){
        if(!_isLogin) return false; 
    }
    if(!obj.isLogin){
        if(_isLogin) return false;
    }

    if(obj.mainPage){
        if(_mainPage != obj.mainPage) return false;
    }
    if(obj.subPage){
        if(_subPage != obj.subPage) return false;
    }

    return true;
}

interface IUsePermission{
    mainPage ?: AllMainPagesType;
    subPage  ?: AllSubPagesType;
    isLogin  ?: boolean;
}
