import { Give_Me_Start_Items } from "../../../../Socket/createAvatar";
import {Copyright, Header, ItemClient, Inventory, Component, Name, useState, useEffectV2, AvatarView, AvatarClient } from "../../../../importAll";


const PageCreateAvatar = new Component((props :IGameSelectAvatarProps)=>{
    let [gender ,setGender] = useState<"boy"|"girl">("girl")
    let [allStartItems , setAllStartItems] = useState<ItemClient[]>([])
    let [avatar , setAvatar] = useState<AvatarClient>(new AvatarClient())
    useEffectV2("GameCreateAvatar",()=>{
        Give_Me_Start_Items(setAllStartItems)
    })

    if(!allStartItems) <>WAIT</>
    return(
        <>
            <Header position="1,1|50,7" border>
                <Name>צור אווטאר</Name>
            </Header>

            <AvatarView avatar={avatar} />

            {/* <Inventory items={allStartItems}/> */}
            

            <Copyright />
        </>
    )
});

interface IGameSelectAvatarProps extends IGlobalProps{}
export default PageCreateAvatar.Get({Login:true,mainPage:"Game",subPage:"Game-CreateAvatar"});
