export default function GetImage(type:any,name:string , ending:string="png"):string{
    let _image :any;
    let folder = "";
    switch(type){
        case "backgroud":
            folder = "backgrounds"; 
            break;
        case "icon":
            folder = "icons"; 
            break;
        case "magic":
            folder = "magicsLogo"; 
            break;
    }
         console.log(`../Images/${folder}/${name}.${ending}`)
        _image = require(`../Images/${folder}/${name}.${ending}`);
