type IType = "backgroud"|"icon"|"magic";

export function GetImage(type:IType,name:string , ending:string ="png"):any{
    let _image :any;
    let folder = "";
    switch(type){
        case "backgroud":
            folder = "backgrounds"; 
            break;
        case "icon":
            folder = "icons"; 
            break;
        case "magic":
            folder = "magicsLogo"; 
            break;
    }
    console.log(`../Images/${folder}/${name}.${ending}`)

    try{
        _image = require(`../Images/${folder}/${name}.${ending}`);
    }
    catch{
        console.log("image not found")
        _image = "ddd"
    }
    finally{
        return _image;
    }
}
