import { useSelector } from "react-redux"

export default function usePermission(obj:IUsePermission){
    let mainPage   = useSelector<IStore,AllMainPagesType>(store=>store.mainPage)
    let subPage    = useSelector<IStore,AllSubPagesType>(store=>store.subPage)
    let isLogin    = useSelector<IStore,boolean>(store=>store.isLogin)

    let premission:boolean = true;

    if(obj.isLogin){
        if(!isLogin) premission= false; 
    }
    if(!obj.isLogin){
        if(isLogin) premission= false;
    }

    if(obj.mainPage){
        if(mainPage != obj.mainPage) premission= false;
    }
    if(obj.subPage){
        if(subPage != obj.subPage) premission= false;
    }

    return {
        mainPage,
        subPage ,
        isLogin ,
        premission
    };
}

interface IUsePermission{
    mainPage ?: AllMainPagesType;
    subPage  ?: AllSubPagesType;
    Login    ?: boolean;
    Logout   ?: boolean;
}
