type IType = "backgroud"|"icon"|"magic";

export default function GetImage(type:IType,name:string , ending:string ="png"):any{
    let image :any;
    let folder = "";
    switch(type){
        case "backgroud":
            folder = "backgrounds"; 
            break;
        case "icon":
            folder = "icons"; 
            break;
        case "magic":
            folder = "magicsLogo"; 
            break;
    }


    try{
        image = require(`../Images/${folder}/${name}.${ending}`);
    }
    catch{
        console.log("image not found")
        image = "ddd"
    }
    finally{
        return image;
    }
}
