import React from "react";
import { myStore, usePermission, useSelector } from "./importAll";

export default class Lib{
    public Comp :(props:any)=>JSX.Element;
    public Permissions? : boolean[];
    



    constructor(Comp:(props:any)=>JSX.Element ,permissions?:boolean[]){
        this.Comp = Comp;
        if(permissions)
            this.Permissions = permissions;
    }



    public GetComp(permissions:IUsePermission):(props:any)=>JSX.Element{
        let Com = this.Comp
        
        return (props:any) => {
            let pre = usePermission(permissions)
            if(pre)
                return  <Com {...props}/>
            else 
                return <>X</>
        }
               
    }


}

interface IPermissions{
    Login :boolean,
    Logout :boolean,
    mainPage : AllMainPagesType,
    subPage : AllSubPagesType;
}
