import { Avatar_Give_Me_List } from "../../Socket/AvatarSocket";
import {memo, useSelector, GameCreateAvatar ,GameSelectAvatar, useStore, useState, useEffectV2, Container, Settings} from "./../../importAll";
import MusicEffect from "./comps/MusicEffect";


const Game = memo(()=>{
    let mainPage                    = useSelector<IStore,any>(store=>store.mainPage)
    if(mainPage != "Game") 
        return <></>
    let settings                    = useSelector<IStore,Settings>(store=>store.settings)
    let store                       = useStore();
    let [isloading , setIsLoading]  = useState<boolean>(true);
    

    useEffectV2("Game",()=>Avatar_Give_Me_List(store,setIsLoading)) 
    
    
    return isloading? 
        <>Wait</>
    : ( 
         <Container grid={settings.CONTAINER_APP_GRID} width="110vh" height="90vh" margin="10px auto" border> 
            <GameSelectAvatar/>
            <GameCreateAvatar/>
            <MusicEffect/>
        </Container>
    )
});

export default Game;
