import { ConvertPoint, useStore } from "../../importAll";

export default function Button(props:IButtonProps){
    let {dispatch,actions} = useStore()
    const styleButton :React.CSSProperties={
        display:"inline-flex",
        border :"1px solid",
        cursor:"pointer",
        width: props.width,
        height: props.height,

        gridColumn:`${ConvertPoint(props.start)[0]}/${ConvertPoint(props.end)[0]}`,
        gridRow : `${ConvertPoint(props.start)[1]}/${ConvertPoint(props.end)[1]}`,
        justifyContent:"center",
        alignItems:"center",
        backgroundImage: props.b,
    }
    
 
   

}

interface IButtonProps extends IGlobalProps{
    className?: string;
    width?: number | string;
    height?: number | string;
    bg? : string ;
    value? : string;
    start?: string;
    end?:string;

}
